// تابع کمکی اصلاح شده برای تولید ساختار نهایی
function generateVocabulary() {
  const dataset = [];
  const languages = ['en', 'fa', 'fr', 'de', 'it', 'es', 'ru', 'zh', 'ar', 'ko'];

  // حلقه روی هر سطح
  wordCollections.forEach(collection => {
    const currentLevel = collection.level;

    // حلقه روی آیتم‌های آن سطح
    collection.items.forEach(item => {
      // حلقه روی زبان‌های هدف
      languages.forEach(targetLang => {
        // چک کن که آیا ترجمه این زبان در آبجکت word موجود است
        if (item.word && item.word[targetLang]) {

          // Flatten translations for easier access in background.js
          const flatTranslations = { ...item.word };
          if (item.sentence) {
            Object.keys(item.sentence).forEach(lang => {
              flatTranslations['sentence_' + lang] = item.sentence[lang];
            });
          }

          dataset.push({
            word: item.word[targetLang], // کلمه به زبان هدف
            level: currentLevel,
            lang: targetLang,
            translations: flatTranslations, // تمام ترجمه‌ها (شامل کلمات و جملات)
            sentence: item.sentence ? item.sentence[targetLang] : "" // جمله به زبان هدف
          });
        }
      });
    });
  });

  return dataset;
}
// خروجی نهایی
export const vocabulary = generateVocabulary();
